const modal=document.querySelector(".modal"),contactsBtn=document.querySelector(".contacts-button"),modalClose=document.querySelector(".close"),login=modal.querySelector("[name=login]"),form=modal.querySelector("form"),email=modal.querySelector("[name=email]"),storage=localStorage.getItem("login");contactsBtn.addEventListener("click",function(e){e.preventDefault(),modal.classList.add("modal-show"),login.focus(),storage?(login.value=storage,email.focus()):login.focus()}),modalClose.addEventListener("click",function(e){e.preventDefault(),modal.classList.remove("modal-show"),modal.classList.remove("modal-error")}),window.addEventListener("keydown",function(e){"Esc"!==e.key&&"Escape"!==e.key||modal.classList.contains("modal-show")&&(e.preventDefault(),modal.classList.remove("modal-show"))}),form.addEventListener("submit",function(e){login.value&&email.value||(e.preventDefault(),modal.classList.add("modal-error"))});
